mapper.update:
update si_material_inventory
set material_id = #{materialId,jdbcType=BIGINT},
  `status` = #{status,jdbcType=TINYINT},
  lower_limit = #{lowerLimit,jdbcType=DECIMAL},
  upper_limit = #{upperLimit,jdbcType=DECIMAL}
where material_inventory_id = #{materialInventoryId,jdbcType=BIGINT}

mapper.select:
select
from sm_storage_detail sd left join si_location l on sd.location_id = l.location_id left join si_material m on l.material_id = m.material_id left join si_supplier s on m.supplier_id = s.supplier_id left join si_material_inventory mi on m.material_id = mi.material_id left join dd_measure_unit mu on m.measure_unit_id = mu.measure_unit_id left join si_warehouse w on l.warehouse_id = w.warehouse_id where sd.storage_status = 1
and (m.material_no like '%' #{keyword,jdbcType=CHAR} '%' or m.material_name like '%' #{keyword,jdbcType=CHAR} '%' or m.material_desc like '%' #{keyword,jdbcType=CHAR} '%')
and m.material_type = #{mtype,jdbcType=TINYINT}
and l.warehouse_id = #{wid,jdbcType=BIGINT}
group by m.material_id,w.warehouse_id order by m.create_time desc

select
from sm_storage_detail sd left join si_location l on sd.location_id = l.location_id left join si_material m on l.material_id = m.material_id left join dd_measure_unit mu on m.measure_unit_id = mu.measure_unit_id left join si_warehouse w on l.warehouse_id = w.warehouse_id left join om_outbound_forklift f on sd.rfid = f.rfid where sd.storage_status = 1
and (sd.rfid like '%' #{keyword,jdbcType=CHAR} '%' or m.material_no like '%' #{keyword,jdbcType=CHAR} '%' or m.material_name like '%' #{keyword,jdbcType=CHAR} '%')
and m.material_batch = #{mbatch,jdbcType=CHAR}
and l.warehouse_id = #{wid,jdbcType=BIGINT}
and l.location_id = #{lid,jdbcType=BIGINT}
and f.forklift_id is null
and f.forklift_id is not null
order by sd.storage_time desc

