mapper.select:
select m.material_id as materialId,m.material_no as materialNo,m.material_model as materialModel,m.material_name as materialName,s.supplier_name as supplierName, mu.measure_unit_name as measureUnitName from si_material m left join si_supplier s on m.supplier_id = s.supplier_id left join dd_measure_unit mu on m.measure_unit_id = mu.measure_unit_id where m.dr = 1 and s.supplier_name like '%' #{sname,jdbcType=CHAR} '%' and m.material_name like '%' #{mname,jdbcType=CHAR} '%' and m.material_model like '%' #{mmodel,jdbcType=CHAR} '%' and m.material_no like '%' #{mno,jdbcType=CHAR} '%' and m.material_desc like '%' #{mno,jdbcType=CHAR} '%'
and m.create_time > #{ctime, jdbcType=TIMESTAMP}
order by m.create_time desc

