mapper.select:
select

from sm_storage s
left join sm_receipt_body rb on s.receipt_body_id = rb.receipt_body_id
left join si_material m on rb.material_id = m.material_id
left join dd_material_type mt on m.material_type_id = mt.material_type_id

    and rb.receipt_no like '%' #{rno,jdbcType=CHAR} '%'
    and rb.order_type = #{rtype,jdbcType=TINYINT}
    and m.material_type = #{mtype,jdbcType=TINYINT}
    and s.`status` = #{status,jdbcType=TINYINT}
    and s.`type` = #{type,jdbcType=TINYINT}

order by s.create_time desc
